package com.dnpass.training.java.day1;
public class ArithOper {
	

	public static void main(String[] args) {
		int num1=20; int num2=4;
		// TODO Auto-generated method stub
		System.out.println(num1+num2);
		System.out.println(num1-num2);
		System.out.println(num1*num2);
		System.out.println(num1/num2);
		System.out.println(num1%num2);
	}

}
