package com.dnpass.training.java.day3;

public class VowelOrCons {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch= 'h';
		switch(ch) {
			case 'a':
			case 'e':
			case 'i':
			case 'o':
			case 'u':
				System.out.println("Its an Vowel");
				break;
			default :
				System.out.println("Its a consonant");
				
			
			
			
		}
		
	}

}
